INSERT INTO public.data_set (id, title, description, task_refer) VALUES ('imdb', 'IMDB', 'The IMDb dataset is a binary sentiment analysis dataset consisting of 50,000 reviews from the Internet Movie Database (IMDb) labeled as positive or negative. The dataset contains an even number of positive and negative reviews. Only highly polarizing reviews are considered. A negative review has a score ≤ 4 out of 10, and a positive review has a score ≥ 7 out of 10. No more than 30 reviews are included per movie. Models are evaluated based on Accuracy.', 'sa');
INSERT INTO public.data_set (id, title, description, task_refer) VALUES ('yb', 'Yelp-Binary', 'The Yelp Review dataset consists of more than 500,000 Yelp reviews. There is both a binary and a fine-grained (five-class) version of the dataset. Models are evaluated based on error (1 - Accuracy; lower is better).', 'sa');
